// Simplified Prism autoloader replacement
(function() {
    if (typeof window.Prism !== 'undefined') {
        // Simple syntax highlighting for common languages
        window.Prism.highlightAll = function() {
            var elements = document.querySelectorAll('code[class*="language-"], pre[class*="language-"]');
            for (var i = 0; i < elements.length; i++) {
                var element = elements[i];
                var className = element.className;
                
                // Basic highlighting patterns
                if (className.includes('php')) {
                    element.innerHTML = element.innerHTML
                        .replace(/(&lt;\?php|&lt;\?)/g, '<span style="color: #0000BB;">$1</span>')
                        .replace(/(class|function|public|private|protected)/g, '<span style="color: #007700;">$1</span>');
                }
                
                if (className.includes('javascript')) {
                    element.innerHTML = element.innerHTML
                        .replace(/(function|var|let|const|return)/g, '<span style="color: #0000FF;">$1</span>')
                        .replace(/(true|false|null|undefined)/g, '<span style="color: #905;">$1</span>');
                }
                
                if (className.includes('bash')) {
                    element.innerHTML = element.innerHTML
                        .replace(/^([\$#])/gm, '<span style="color: #666;">$1</span>');
                }
            }
        };
        
        window.Prism.highlightAllUnder = function(container) {
            window.Prism.highlightAll();
        };
    }
})();